mapper.select:
SELECT GROUP_CD as "groupCd" , GROUP_NM as "groupNm" , ITEM_CD as "itemCd" , ITEM_NM as "itemNm" , ORDERED as "ordered" FROM CODES
AND GROUP_CD = #{groupCd}
ORDER BY ORDERED ASC

SELECT GROUP_CD as "groupCd" , GROUP_NM as "groupNm" , ITEM_CD as "itemCd" , ITEM_NM as "itemNm" , ORDERED as "ordered" FROM CODES
AND GROUP_CD = #{groupCd}
ORDER BY ORDERED ASC

