mapper.select:
SELECT GROUP_CD as "groupCd" , GROUP_NM as "groupNm" , ITEM_CD as "itemCd" , ITEM_NM as "itemNm" , ORDERED as "ordered" FROM ORDERS
AND GROUP_CD = #{groupCd}
ORDER BY ORDERED ASC

mapper.insert:
INSERT INTO
	ORDERS
	(
			GROUP_CD
		, GROUP_NM
		, ITEM_CD
		, ITEM_NM
		, ORDERED
	)
VALUES 
	(
		  #{groupCd,jdbcType=VARCHAR}   
		, #{groupNm,jdbcType=VARCHAR} 
		, #{itemCd,jdbcType=VARCHAR} 
		, #{itemNm,jdbcType=VARCHAR} 
		, #{ordered,jdbcType=VARCHAR}				
	)

mapper.update:
/* 코드 관리 수정 */
UPDATE
		ORDERS

	 GROUP_CD  = #{groupCd}   ,  
	 GROUP_NM  = #{groupNm}   ,  
	 ITEM_CD   = #{itemCd}    ,  
	 ITEM_NM   = #{itemNm}    ,  
	 ORDERED   = #{ordered}   ,  


	AND GROUP_CD = #{groupCd}
	AND ITEM_CD = #{itemCd}

mapper.delete:
/* 코드 삭제 */
		DELETE FROM
		ORDERS

	AND GROUP_CD = #{groupCd}
	AND ITEM_CD = #{itemCd}
