mapper.select:
SELECT ORDER_ID as "orderId" , ITEM_CLS as "itemCls" , COMPANY as "company" , ITEM_NM as "itemNm" , ITEM_SPEC as "itemSpec" , TOTAL_QTY as "totalQty" , PAPER1 as "paper1" , PAPER2 as "paper2" , PAPER3 as "paper3" , PAPER4 as "paper4" , PARER_ROLL as "parerRoll" , ROLL_QTY as "rollQty" , DUE_DATE as "dueDate" , DELIVERY as "delivery" , DESCRIPTION as "description" , FILE1 as "file1" , FILE2 as "file2" , USER_ID as "userId" , STR_TO_DATE(NEW_DATETIME,'%Y-%m-%d') as "newDate" , STR_TO_DATE(NEW_DATETIME,'%H:%i:%S') as "newTime" , STR_TO_DATE(MOD_DATETIME,'%Y-%m-%d') as "modDate" , STR_TO_DATE(MOD_DATETIME,'%H:%i:%S') as "modTime" , STR_TO_DATE(END_DATETIME,'%Y-%m-%d') as "endDate" , STR_TO_DATE(END_DATETIME,'%H:%i:%S') as "endTime" FROM ORDERS
AND GROUP_CD = #{groupCd}
ORDER BY ORDERED ASC

mapper.insert:
/* 주문 입력 */
INSERT INTO
	ORDERS
	(
		  ITEM_CLS   
		, COMPANY    
		, ITEM_NM    
		, ITEM_SPEC  
		, TOTAL_QTY  
		, PAPER1     
		, PAPER2     
		, PAPER3     
		, PAPER4     
		, PARER_ROLL 
		, ROLL_QTY   
		, DUE_DATE   
		, DELIVERY   
		, DESCRIPTION
		, FILE1      
		, FILE2      
		, USER_ID    
		, NEW_DATETIME
	)
VALUES 
	(
		  #{itemCls,jdbcType=VARCHAR}
		, #{company,jdbcType=VARCHAR}
		, #{itemNm,jdbcType=VARCHAR}
		, #{itemSpec,jdbcType=VARCHAR}
		, #{totalQty,jdbcType=INT}
		, #{paper1,jdbcType=VARCHAR}
		, #{paper2,jdbcType=VARCHAR}
		, #{paper3,jdbcType=VARCHAR}
		, #{paper4,jdbcType=VARCHAR}
		, #{parerRoll,jdbcType=VARCHAR}
		, #{rollQty,jdbcType=INT}
		, #{dueDate,jdbcType=VARCHAR}
		, #{delivery,jdbcType=VARCHAR}
		, #{description,jdbcType=VARCHAR}
		, #{file1,jdbcType=VARCHAR}
		, #{file2,jdbcType=VARCHAR}
		, #{userId,jdbcType=VARCHAR}
		, now()
	)

mapper.update:
/* 주문 수정 */ UPDATE ORDERS
ITEM_CLS = #{itemCls} , COMPANY = #{company} , ITEM_NM = #{itemNm} , ITEM_SPEC = #{itemSpec} , TOTAL_QTY = #{totalQty} , PAPER1 = #{paper1} , PAPER2 = #{paper2} , PAPER3 = #{paper3} , PAPER4 = #{paper4} , PARER_ROLL = #{parerRoll} , ROLL_QTY = #{rollQty} , DUE_DATE = #{dueDate} , DELIVERY = #{delivery} , DESCRIPTION = #{description} , FILE1 = #{file1} , FILE2 = #{file2} , USER_ID = #{userId} , END_DATETIME = now() , MOD_DATETIME = now(),
AND ORDER_ID = #{orderId}

mapper.delete:
/* 주문 삭제 */
DELETE FROM
		ORDERS

	AND ORDER_ID = #{orderId}
