mapper.insert:
/* 사용자 입력 */
INSERT INTO
	USERS
	(
		  USER_ID    
		, USER_PW  
		, USER_COMP  
		, USER_CLS   
		, NEW_DT     
		, MOD_DT
	)
	VALUES 
	(
		  #{userId,jdbcType=VARCHAR}    
		, #{userPw,jdbcType=VARCHAR}
		, #{userComp,jdbcType=VARCHAR}  
		, #{userCls,jdbcType=VARCHAR}   
		, now() 
		, now()    
	)

mapper.update:
/* 사용자 수정 */ UPDATE USERS
USER_PW = #{userPw} , USER_COMP = #{userComp} , MOD_DT = now(),
AND USER_ID = #{userId}

mapper.delete:
/* 코드 삭제 */
DELETE FROM
	USERS

	AND USER_ID = #{userId}
	AND USER_PW = #{userPw}
